import React, { useState } from "react";
import ChannelList from "../components/ChannelList";
import ChatWindow from "../components/ChatWindow";
import InputBar from "../components/InputBar";
import UserList from "../components/UserList";
import VoicePanel from "../components/VoicePanel";

function ChatPage() {
  const [channel, setChannel] = useState("general");

  return (
    <div style={{ display: "flex", height: "100vh", background: "#202225" }}>
      
      {/* LEFT SIDEBAR */}
      <div style={{ 
        width: 260, 
        display: "flex", 
        flexDirection: "column", 
        background: "#2f3136" 
      }}>
        
        {/* CHANNEL LIST */}
        <div style={{ flex: 1, overflowY: "auto" }}>
          <ChannelList currentChannel={channel} onSelect={setChannel} />
        </div>

        {/* VOICE PANEL (BOTTOM LEFT) */}
        <div style={{ 
          height: 90, 
          borderTop: "1px solid #1f1f1f",
          background: "#292b2f"
        }}>
          <VoicePanel channel={channel} />
        </div>
      </div>

      {/* MAIN CHAT AREA */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <ChatWindow channel={channel} />
        <InputBar channel={channel} />
      </div>

      {/* RIGHT SIDEBAR */}
      <div style={{ width: 250, background: "#2f3136" }}>
        <UserList />
      </div>
    </div>
  );
}

export default ChatPage;
