import React, { useState, useEffect } from "react";
import { socket } from "../socket";

function ChatWindow({ channel }) {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Nachrichten leeren
    setMessages([]);

    // Channel beitreten
    socket.emit("joinChannel", channel);

    // Historie empfangen
    const historyHandler = (history) => {
      setMessages(history);
    };

    // Neue Nachrichten empfangen
    const messageHandler = (msg) => {
      if (msg.channel === channel) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("channelHistory", historyHandler);
    socket.on("chatMessage", messageHandler);

    return () => {
      socket.off("channelHistory", historyHandler);
      socket.off("chatMessage", messageHandler);
    };
  }, [channel]);

  return (
    <div
      style={{
        flex: 1,
        padding: 20,
        overflowY: "auto",
        background: "#fff",
        height: "100%",
      }}
    >
      {messages.map((msg, i) => (
        <div key={i} style={{ marginBottom: 10 }}>
          <strong>{msg.name}: </strong>
          {msg.text}
        </div>
      ))}
    </div>
  );
}

export default ChatWindow;
