import React, { useState, useEffect } from "react";
import { socket } from "../socket";

export default function ChannelList({ currentChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState(["admin", "general", "gaming", "music"]);
  const [voiceChannelKeys, setVoiceChannelKeys] = useState(["admin", "general", "gaming", "music"]);
  const [activeVoice, setActiveVoice] = useState(null);

  useEffect(() => {
    const handleUpdate = ({ textChannels, voiceChannels }) => {
      setTextChannels(textChannels);
      setVoiceChannelKeys(Object.keys(voiceChannels));
    };

    socket.on("channelListUpdate", handleUpdate);

    return () => {
      socket.off("channelListUpdate", handleUpdate);
    };
  }, []);

  const createChannel = () => {
    const name = window.prompt("Neuer Channelname:");
    if (name && name.trim()) {
      socket.emit("createChannel", { name: name.trim() });
    }
  };

  const renameChannel = (oldName) => {
    const newName = window.prompt("Neuer Name:", oldName);
    if (newName && newName.trim()) {
      socket.emit("renameChannel", { oldName, newName: newName.trim() });
    }
  };

  const deleteChannel = (name) => {
    if (window.confirm(`Channel "${name}" lÃ¶schen?`)) {
      socket.emit("deleteChannel", { name });
    }
  };

  const joinVoice = (name) => {
    const channel = name.toLowerCase();

    if (activeVoice !== channel) {
      if (activeVoice) {
        socket.emit("leaveVoiceChannel", { channel: activeVoice });
      }

      socket.emit("joinVoiceChannel", {
        channel,
        username: localStorage.getItem("username") || "Unbekannt"
      });

      setActiveVoice(channel);
      localStorage.setItem("voiceChannel", channel);
    }
  };

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div
        onClick={createChannel}
        style={{
          padding: "6px 10px",
          borderRadius: 4,
          background: "#3a3c41",
          cursor: "pointer",
          marginBottom: 10,
          textAlign: "center"
        }}
      >
        + Channel
      </div>

      <div style={{ marginBottom: 10, opacity: 0.6 }}>TEXT CHANNELS</div>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          onContextMenu={(e) => {
            e.preventDefault();
            const action = window.prompt("1 = Umbenennen, 2 = LÃ¶schen");
            if (action === "1") renameChannel(ch);
            if (action === "2") deleteChannel(ch);
          }}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            background: currentChannel === ch ? "#3a3c41" : "transparent",
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ marginTop: 20, marginBottom: 10, opacity: 0.6 }}>VOICE CHANNELS</div>

      {voiceChannelKeys.map((vc) => (
        <div
          key={vc}
          onClick={() => joinVoice(vc)}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            background: activeVoice === vc.toLowerCase() ? "#3a3c41" : "transparent",
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          ðŸ”Š {vc}
        </div>
      ))}
    </div>
  );
}
