import React, { useEffect, useState } from "react";
import { socket } from "../socket";
import { getAvatar } from "../utils/avatar";

function UserList() {
  const [users, setUsers] = useState({});

  useEffect(() => {
    socket.on("userList", (list) => {
      setUsers(list);
    });

    return () => {
      socket.off("userList");
    };
  }, []);

  return (
    <div style={{ width: 200, background: "#202225", color: "#fff", padding: 20 }}>
      <h3>Online</h3>

      {Object.values(users).map((name, i) => {
        const avatar = getAvatar(name);

        return (
          <div key={i} style={{ display: "flex", alignItems: "center", marginBottom: 10 }}>
            <div
              style={{
                width: 32,
                height: 32,
                borderRadius: "50%",
                background: avatar.color,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                marginRight: 10,
                color: "#fff",
                fontWeight: "bold"
              }}
            >
              {avatar.initial}
            </div>
            {name}
          </div>
        );
      })}
    </div>
  );
}

export default UserList;
