import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function ChannelList({ selectedChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState([]);
  const [voiceChannels, setVoiceChannels] = useState({});

  useEffect(() => {
    const update = ({ textChannels, voiceChannels }) => {
      setTextChannels(textChannels);
      setVoiceChannels(voiceChannels);
    };

    socket.on("channelListUpdate", update);
    socket.emit("requestChannelList");

    return () => socket.off("channelListUpdate", update);
  }, []);

  const createChannel = () => {
    const name = prompt("Neuer Channelname:");
    if (!name) return;
    socket.emit("createChannel", { name });
  };

  const renameChannel = (oldName) => {
    const newName = prompt("Neuer Name fÃ¼r Channel:", oldName);
    if (!newName) return;
    socket.emit("renameChannel", { oldName, newName });
  };

  const deleteChannel = (name) => {
    if (!window.confirm(`Channel "${name}" lÃ¶schen?`)) return;
    socket.emit("deleteChannel", { name });
  };

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>TEXT CHANNELS</div>

      <button
        onClick={createChannel}
        style={{
          width: "100%",
          padding: 6,
          background: "#5865F2",
          border: "none",
          borderRadius: 4,
          color: "white",
          cursor: "pointer",
          marginBottom: 10
        }}
      >
        + Channel hinzufÃ¼gen
      </button>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          onContextMenu={(e) => {
            e.preventDefault();
            const action = prompt("1 = Umbenennen\n2 = LÃ¶schen");
            if (action === "1") renameChannel(ch);
            if (action === "2") deleteChannel(ch);
          }}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            background: selectedChannel === ch ? "#3a3b3f" : "transparent",
            borderRadius: 4,
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ opacity: 0.6, marginTop: 20, marginBottom: 10 }}>
        VOICE CHANNELS
      </div>

      {Object.keys(voiceChannels).map((ch) => (
        <div
          key={ch}
          onClick={() => {
            const username = localStorage.getItem("username");
            socket.emit("joinVoiceChannel", { channel: ch, username });
          }}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            borderRadius: 4,
            marginBottom: 4,
            background: "#2f3136"
          }}
        >
          ðŸ”Š {ch}
        </div>
      ))}
    </div>
  );
}
