import React, { useState, useRef } from "react";
import { socket } from "../socket";
import { startCall, muteLocalAudio, unmuteLocalAudio, isLocalMuted } from "../webrtc";

export default function VoicePanel() {
  const [inCall, setInCall] = useState(false);
  const [muted, setMuted] = useState(false);
  const [speaking, setSpeaking] = useState(false);
  const remoteAudio = useRef(null);

  socket.on("speakingStart", ({ user }) => {
    if (user === localStorage.getItem("username")) setSpeaking(true);
  });

  socket.on("speakingStop", ({ user }) => {
    if (user === localStorage.getItem("username")) setSpeaking(false);
  });

  const startVoice = async () => {
    await startCall((stream) => {
      remoteAudio.current.srcObject = stream;
    });
    setInCall(true);
  };

  const stopVoice = () => {
    setInCall(false);
  };

  const toggleMute = () => {
    if (isLocalMuted()) {
      unmuteLocalAudio();
      setMuted(false);
    } else {
      muteLocalAudio();
      setMuted(true);
    }
  };

  return (
    <div style={{
      height: "90px",
      background: "#232428",
      display: "flex",
      alignItems: "center",
      padding: "10px",
      color: "white"
    }}>
      <div className={speaking ? "speaking-ring" : ""} style={{
        width: 40,
        height: 40,
        borderRadius: "50%",
        overflow: "hidden",
        marginRight: 10
      }}>
        <img
          src="/avatar.png"
          className="avatar"
        />
      </div>

      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: "bold" }}>{localStorage.getItem("username")}</div>
        <div style={{ fontSize: 12, color: "#b9bbbe" }}>
          {muted ? "Muted" : speaking ? "Speaking..." : "Connected"}
        </div>
      </div>

      {!inCall && (
        <button onClick={startVoice} style={btnStyle}>üìû</button>
      )}

      {inCall && (
        <>
          <button onClick={toggleMute} style={btnStyle}>
            {muted ? "üîá" : "üé§"}
          </button>
          <button onClick={stopVoice} style={btnStyle}>‚ùå</button>
        </>
      )}

      <audio ref={remoteAudio} autoPlay />
    </div>
  );
}

const btnStyle = {
  background: "transparent",
  border: "none",
  color: "white",
  fontSize: 20,
  cursor: "pointer",
  marginLeft: 8
};
