import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function ChannelList({ selectedChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState([]);
  const [voiceChannels, setVoiceChannels] = useState({});
  const [contextMenu, setContextMenu] = useState(null);

  useEffect(() => {
    const update = ({ textChannels, voiceChannels }) => {
      setTextChannels(textChannels);
      setVoiceChannels(voiceChannels);
    };

    socket.on("channelListUpdate", update);
    socket.emit("requestChannelList");

    return () => socket.off("channelListUpdate", update);
  }, []);

  const createChannel = () => {
    const name = prompt("Neuer Channelname:");
    if (!name) return;
    socket.emit("createChannel", { name });
  };

  const renameChannel = (oldName) => {
    const newName = prompt("Neuer Name fÃ¼r #" + oldName);
    if (!newName) return;
    socket.emit("renameChannel", { oldName, newName });
  };

  const deleteChannel = (name) => {
    if (!window.confirm(`Channel #${name} lÃ¶schen?`)) return;
    socket.emit("deleteChannel", { name });
  };

  const joinVoice = (ch) => {
    const username = localStorage.getItem("username");
    socket.emit("joinVoiceChannel", { channel: ch, username });
  };

  const openContextMenu = (e, channel) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      channel
    });
  };

  const closeContextMenu = () => setContextMenu(null);

  return (
    <div style={{ padding: 10, color: "white", position: "relative" }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>TEXT CHANNELS</div>

      <button
        onClick={createChannel}
        style={{
          width: "100%",
          padding: 6,
          background: "#5865F2",
          border: "none",
          borderRadius: 4,
          color: "white",
          cursor: "pointer",
          marginBottom: 10
        }}
      >
        + Channel hinzufÃ¼gen
      </button>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          onContextMenu={(e) => openContextMenu(e, ch)}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            background: selectedChannel === ch ? "#3a3b3f" : "transparent",
            borderRadius: 4,
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ opacity: 0.6, margin: "20px 0 10px" }}>VOICE CHANNELS</div>

      {Object.keys(voiceChannels).map((ch) => (
        <div
          key={ch}
          onClick={() => joinVoice(ch)}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            borderRadius: 4,
            marginBottom: 4
          }}
        >
          ðŸ”Š {ch}
        </div>
      ))}

      {contextMenu && (
        <div
          style={{
            position: "fixed",
            top: contextMenu.y,
            left: contextMenu.x,
            background: "#2b2d31",
            padding: 10,
            borderRadius: 4,
            zIndex: 999,
            boxShadow: "0 0 10px rgba(0,0,0,0.5)"
          }}
          onMouseLeave={closeContextMenu}
        >
          <div
            style={{ padding: 5, cursor: "pointer" }}
            onClick={() => {
              renameChannel(contextMenu.channel);
              closeContextMenu();
            }}
          >
            Umbenennen
          </div>
          <div
            style={{ padding: 5, cursor: "pointer", color: "red" }}
            onClick={() => {
              deleteChannel(contextMenu.channel);
              closeContextMenu();
            }}
          >
            LÃ¶schen
          </div>
        </div>
      )}
    </div>
  );
}
