import React, { useEffect, useState, useRef } from "react";
import { socket } from "../socket";

export default function ChatWindow({ channel }) {
  const [messages, setMessages] = useState([]);
  const [menu, setMenu] = useState(null); // {x, y, index}
  const username = localStorage.getItem("username");
  const scrollRef = useRef(null);

  useEffect(() => {
    socket.emit("requestMessages", { channel });
  }, [channel]);

  useEffect(() => {
    const load = (msgs) => setMessages(msgs);
    const add = (msg) => {
      if (msg.channel === channel) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("loadMessages", load);
    socket.on("newMessage", add);

    const closeMenu = () => setMenu(null);
    window.addEventListener("click", closeMenu);

    return () => {
      socket.off("loadMessages", load);
      socket.off("newMessage", add);
      window.removeEventListener("click", closeMenu);
    };
  }, [channel]);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    const input = document.getElementById("chatInput");
    const text = input.value.trim();
    if (!text) return;

    socket.emit("sendMessage", {
      channel,
      user: username,
      text
    });

    input.value = "";
  };

  const editMessage = (index, oldText) => {
    const newText = prompt("Neue Nachricht:", oldText);
    if (!newText) return;
    socket.emit("editMessage", { channel, index, newText });
  };

  const deleteMessage = (index) => {
    if (!window.confirm("Nachricht löschen?")) return;
    socket.emit("deleteMessage", { channel, index });
  };

  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column", position: "relative" }}>
      <div
        ref={scrollRef}
        style={{
          flex: 1,
          overflowY: "auto",
          padding: 20,
          color: "white"
        }}
      >
        <h3># {channel}</h3>

        {messages.map((m, i) => (
          <div
            key={i}
            onContextMenu={(e) => {
              if (m.user !== username) return;
              e.preventDefault();
              setMenu({ x: e.clientX, y: e.clientY, index: i });
            }}
            style={{ marginBottom: 10 }}
          >
            <b>{m.user}:</b> {m.text}
          </div>
        ))}
      </div>

      {menu && (
        <div
          className="context-menu"
          style={{ top: menu.y, left: menu.x }}
        >
          <div
            className="context-menu-item"
            onClick={() => editMessage(menu.index, messages[menu.index].text)}
          >
            Bearbeiten
          </div>
          <div
            className="context-menu-item"
            onClick={() => deleteMessage(menu.index)}
          >
            Löschen
          </div>
        </div>
      )}

      <div
        style={{
          display: "flex",
          padding: 10,
          background: "#232428",
          borderTop: "1px solid #3a3b3f"
        }}
      >
        <input
          id="chatInput"
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          placeholder={`Nachricht in #${channel}...`}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 4,
            border: "none",
            outline: "none"
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            marginLeft: 10,
            padding: "10px 20px",
            background: "#5865F2",
            border: "none",
            borderRadius: 4,
            color: "white",
            cursor: "pointer"
          }}
        >
          Senden
        </button>
      </div>
    </div>
  );
}
