import React, { useState, useEffect } from "react";
import { socket } from "../socket";
import ReactMarkdown from "react-markdown";
import { getAvatar } from "../utils/avatar";

function ChatWindow({ channel }) {
  const [messages, setMessages] = useState([]);
  const [typingUser, setTypingUser] = useState(null);

  useEffect(() => {
    setMessages([]);

    socket.emit("joinChannel", channel);

    const historyHandler = (history) => {
      setMessages(history);
    };

    const messageHandler = (msg) => {
      if (msg.channel === channel) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    const deleteHandler = (id) => {
      setMessages((prev) => prev.filter((m) => m.id !== id));
    };

    const editHandler = ({ id, newText }) => {
      setMessages((prev) =>
        prev.map((m) => (m.id === id ? { ...m, text: newText } : m))
      );
    };

    const typingHandler = (username) => {
      setTypingUser(username);
      setTimeout(() => setTypingUser(null), 2000);
    };

    socket.on("channelHistory", historyHandler);
    socket.on("chatMessage", messageHandler);
    socket.on("messageDeleted", deleteHandler);
    socket.on("messageEdited", editHandler);
    socket.on("typing", typingHandler);

    return () => {
      socket.off("channelHistory", historyHandler);
      socket.off("chatMessage", messageHandler);
      socket.off("messageDeleted", deleteHandler);
      socket.off("messageEdited", editHandler);
      socket.off("typing", typingHandler);
    };
  }, [channel]);

  const username = localStorage.getItem("username");

  const deleteMessage = (id) => {
    socket.emit("deleteMessage", id);
  };

  const editMessage = (id) => {
    const newText = prompt("Neue Nachricht eingeben:");
    if (newText && newText.trim() !== "") {
      socket.emit("editMessage", { id, newText });
    }
  };

  return (
    <div style={{ flex: 1, padding: 20, overflowY: "auto", background: "#fff" }}>
      {messages.map((msg) => {
        const avatar = getAvatar(msg.username);

        return (
          <div key={msg.id} style={{ marginBottom: 20, display: "flex" }}>
            <div
              style={{
                width: 40,
                height: 40,
                borderRadius: "50%",
                background: avatar.color,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                marginRight: 10,
                color: "#fff",
                fontWeight: "bold"
              }}
            >
              {avatar.initial}
            </div>

            <div>
              <strong>{msg.username}</strong>
              <span style={{ color: "#888", marginLeft: 10 }}>
                {new Date(msg.timestamp).toLocaleTimeString()}
              </span>

              <div style={{ marginTop: 5 }}>
                <ReactMarkdown>{msg.text}</ReactMarkdown>
              </div>

              {msg.username === username && (
                <div style={{ marginTop: 5 }}>
                  <button onClick={() => editMessage(msg.id)} style={{ marginRight: 10 }}>
                    Bearbeiten
                  </button>
                  <button onClick={() => deleteMessage(msg.id)}>Löschen</button>
                </div>
              )}
            </div>
          </div>
        );
      })}

      {typingUser && (
        <div style={{ color: "#888", marginTop: 10 }}>
          {typingUser} schreibt…
        </div>
      )}
    </div>
  );
}

export default ChatWindow;
