import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function UserList() {
  const [voiceChannels, setVoiceChannels] = useState({});
  const [users, setUsers] = useState({});
  const [speakingUsers, setSpeakingUsers] = useState({});

  useEffect(() => {
    const vcHandler = (vc) => setVoiceChannels(vc);
    const avatarHandler = (allUsers) => setUsers(allUsers);

    const speakingStart = ({ user }) =>
      setSpeakingUsers((prev) => ({ ...prev, [user]: true }));

    const speakingStop = ({ user }) =>
      setSpeakingUsers((prev) => ({ ...prev, [user]: false }));

    socket.on("voiceChannelUpdate", vcHandler);
    socket.on("avatarUpdate", avatarHandler);
    socket.on("speakingStart", speakingStart);
    socket.on("speakingStop", speakingStop);

    return () => {
      socket.off("voiceChannelUpdate", vcHandler);
      socket.off("avatarUpdate", avatarHandler);
      socket.off("speakingStart", speakingStart);
      socket.off("speakingStop", speakingStop);
    };
  }, []);

  return (
    <div>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>VOICE USERS</div>

      {Object.entries(voiceChannels).map(([channel, list]) => {
        if (!list.length) return null;

        return (
          <div key={channel} style={{ marginBottom: 15 }}>
            <div style={{ fontSize: 12, opacity: 0.7, marginBottom: 4 }}>
              {channel}
            </div>

            {list.map((u) => {
              const av = users[u.username] || {
                avatarType: "default",
                avatarColor: "#5865F2",
                avatarLetter: u.username[0]?.toUpperCase() || "?",
                avatarImage: null
              };

              const isSpeaking = speakingUsers[u.username];

              return (
                <div
                  key={u.id}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: 4
                  }}
                >
                  <div
                    className={isSpeaking ? "speaking-ring" : ""}
                    style={{
                      width: 28,
                      height: 28,
                      borderRadius: "50%",
                      overflow: "hidden",
                      marginRight: 8,
                      background:
                        av.avatarType === "default"
                          ? av.avatarColor
                          : "transparent",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      fontWeight: "bold",
                      fontSize: 14,
                      color: "white"
                    }}
                  >
                    {av.avatarType === "image" ? (
                      <img src={av.avatarImage} className="avatar" alt="" />
                    ) : (
                      av.avatarLetter
                    )}
                  </div>

                  <div style={{ fontSize: 14 }}>{u.username}</div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
