// src/components/UserList.jsx
import React, { useEffect, useState } from "react";
import socket from "../socket";

const API_BASE = "http://localhost:3000";

function UserList() {
  const [users, setUsers] = useState([]);
  const [online, setOnline] = useState([]);

  async function loadUsers() {
    try {
      const res = await fetch(`${API_BASE}/api/users/list`);
      const data = await res.json();
      if (data.ok) {
        setUsers(data.users);
      }
    } catch (err) {
      console.error("Fehler beim Laden der User:", err);
    }
  }

  useEffect(() => {
    loadUsers();

    function handleOnline({ userId }) {
      setOnline((prev) => [...new Set([...prev, userId])]);
    }

    function handleOffline({ userId }) {
      setOnline((prev) => prev.filter((id) => id !== userId));
    }

    socket.on("userOnline", handleOnline);
    socket.on("userOffline", handleOffline);

    return () => {
      socket.off("userOnline", handleOnline);
      socket.off("userOffline", handleOffline);
    };
  }, []);

  return (
    <div className="user-list">
      <h3>Online</h3>
      {users
        .filter((u) => online.includes(u.id))
        .map((u) => (
          <div key={u.id} className="user-online">
            ● {u.username}
          </div>
        ))}

      <h3>Offline</h3>
      {users
        .filter((u) => !online.includes(u.id))
        .map((u) => (
          <div key={u.id} className="user-offline">
            ○ {u.username}
          </div>
        ))}
    </div>
  );
}

export default UserList;
