import React, { useEffect, useState } from "react";
import { socket } from "./socket";
import "./App.css";

import ChannelList from "./components/ChannelList";
import ChatWindow from "./components/ChatWindow";
import UserList from "./components/UserList";
import VoicePanel from "./components/VoicePanel";

function App() {
  const [selectedChannel, setSelectedChannel] = useState("admin");

  useEffect(() => {
    let username = localStorage.getItem("username");

    if (!username || username.trim() === "") {
      username = prompt("Bitte gib deinen Namen ein:");
      if (!username || username.trim() === "") {
        username = "User" + Math.floor(Math.random() * 9999);
      }
      localStorage.setItem("username", username);
    }

    // Richtiger Aufruf statt registerUser()
    socket.emit("registerUser", { username });
  }, []);

  return (
    <div className="App">
      {/* Linke Sidebar */}
      <div className="sidebar">
        <ChannelList
          selectedChannel={selectedChannel}
          onSelect={(ch) => setSelectedChannel(ch)}
        />

        {/* VoicePanel unten links */}
        <div className="voice-panel-container">
          <VoicePanel />
        </div>
      </div>

      {/* Chat-Bereich */}
      <div className="chat-container">
        <ChatWindow channel={selectedChannel} />
      </div>

      {/* Rechte Voice-User-Liste */}
      <div className="userlist">
        <UserList />
      </div>
    </div>
  );
}

export default App;
