import React, { useState, useEffect, useRef } from "react";
import { socket } from "../socket";
import { uploadAvatar, updateAvatarOnServer, getAvatarFromServer } from "../avatar";

export default function VoicePanel() {
  const username = localStorage.getItem("username");
  const [users, setUsers] = useState({});
  const [voiceChannel, setVoiceChannel] = useState(null);
  const [speaking, setSpeaking] = useState(false);
  const [muted, setMuted] = useState(false);

  const [debugAvg, setDebugAvg] = useState(0);
  const threshold = 10;

  const streamRef = useRef(null);
  const analyserRef = useRef(null);
  const audioContextRef = useRef(null);
  const rafRef = useRef(null);
  const lastSpeaking = useRef(false);

  useEffect(() => {
    socket.on("avatarUpdate", setUsers);
    return () => socket.off("avatarUpdate");
  }, []);

  const avatar = getAvatarFromServer(users, username);

  useEffect(() => {
    socket.on("voiceChannelUpdate", (channels) => {
      let found = null;

      Object.entries(channels).forEach(([ch, list]) => {
        if (list.some((u) => u.username === username)) found = ch;
      });

      if (found && !voiceChannel) startMic(found);
      if (!found && voiceChannel) stopMic();

      setVoiceChannel(found);
    });

    return () => socket.off("voiceChannelUpdate");
  }, [voiceChannel]);

  const startMic = async (channel) => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    streamRef.current = stream;

    const ctx = new AudioContext();
    const source = ctx.createMediaStreamSource(stream);

    const gain = ctx.createGain();
    gain.gain.value = 2.0;

    const analyser = ctx.createAnalyser();
    analyser.fftSize = 1024;

    source.connect(gain);
    gain.connect(analyser);

    audioContextRef.current = ctx;
    analyserRef.current = analyser;

    detectSpeaking(channel);
  };

  const stopMic = () => {
    if (rafRef.current) cancelAnimationFrame(rafRef.current);
    if (audioContextRef.current) audioContextRef.current.close();
    if (streamRef.current) streamRef.current.getTracks().forEach((t) => t.stop());

    setSpeaking(false);
    lastSpeaking.current = false;
  };

  const detectSpeaking = (channel) => {
    const analyser = analyserRef.current;
    const data = new Uint8Array(analyser.frequencyBinCount);

    const loop = () => {
      analyser.getByteFrequencyData(data);
      const avg = data.reduce((a, b) => a + b, 0) / data.length;

      setDebugAvg(avg.toFixed(1));

      const isSpeaking = avg > threshold && !muted;

      if (isSpeaking !== lastSpeaking.current) {
        lastSpeaking.current = isSpeaking;
        setSpeaking(isSpeaking);

        socket.emit(isSpeaking ? "speakingStart" : "speakingStop", {
          channel,
          user: username
        });
      }

      rafRef.current = requestAnimationFrame(loop);
    };

    loop();
  };

  const disconnect = () => {
    if (!voiceChannel) return;
    socket.emit("leaveVoiceChannel", { channel: voiceChannel });
  };

  const handleAvatarRightClick = (e) => {
    e.preventDefault();
    uploadAvatar((base64) => {
      updateAvatarOnServer(username, {
        avatarType: "image",
        avatarColor: avatar.avatarColor,
        avatarLetter: avatar.avatarLetter,
        avatarImage: base64
      });
    });
  };

  return (
    <div style={{ height: 130, background: "#232428", display: "flex", flexDirection: "column", padding: 10, color: "white" }}>
      
      <div style={{ display: "flex", alignItems: "center" }}>
        <div
          onContextMenu={handleAvatarRightClick}
          className={speaking ? "speaking-ring" : ""}
          style={{
            width: 40,
            height: 40,
            borderRadius: "50%",
            overflow: "hidden",
            marginRight: 10,
            background: avatar.avatarType === "default" ? avatar.avatarColor : "transparent",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontWeight: "bold",
            fontSize: 20,
            cursor: "pointer",
            position: "relative"
          }}
        >
          {avatar.avatarType === "image" ? (
            <img src={avatar.avatarImage} className="avatar" alt="" />
          ) : (
            avatar.avatarLetter
          )}
        </div>

        <div style={{ flex: 1 }}>
          <div style={{ fontWeight: "bold" }}>{username}</div>
          <div style={{ fontSize: 12, color: "#b9bbbe" }}>
            {!voiceChannel
              ? "Nicht verbunden"
              : speaking
              ? `Spricht in ${voiceChannel}`
              : `Verbunden in ${voiceChannel}`}
          </div>
        </div>

        {voiceChannel && (
          <button
            onClick={disconnect}
            style={{
              background: "transparent",
              border: "none",
              color: "white",
              fontSize: 20,
              cursor: "pointer",
              marginLeft: 8
            }}
          >
            ❌
          </button>
        )}
      </div>

      <div style={{ marginTop: 8, fontSize: 12, opacity: 0.8 }}>
        Lautstärke: {debugAvg}
        <br />
        Threshold: {threshold}
        <br />
        Speaking: {speaking ? "true" : "false"}
      </div>
    </div>
  );
}
