import React, { useEffect, useState, useRef } from "react";
import { socket } from "../socket";

export default function ChatWindow({ channel }) {
  const [messages, setMessages] = useState([]);
  const username = localStorage.getItem("username");
  const scrollRef = useRef(null);

  useEffect(() => {
    socket.emit("requestMessages", { channel });
  }, [channel]);

  useEffect(() => {
    const load = (msgs) => setMessages(msgs);
    const add = (msg) => {
      if (msg.channel === channel) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("loadMessages", load);
    socket.on("newMessage", add);

    return () => {
      socket.off("loadMessages", load);
      socket.off("newMessage", add);
    };
  }, [channel]);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    const input = document.getElementById("chatInput");
    const text = input.value.trim();
    if (!text) return;

    socket.emit("sendMessage", {
      channel,
      user: username,
      text
    });

    input.value = "";
  };

  const handleKey = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
      <div
        ref={scrollRef}
        style={{
          flex: 1,
          overflowY: "auto",
          padding: 20,
          color: "white"
        }}
      >
        <h3># {channel}</h3>

        {messages.map((m, i) => (
          <div key={i} style={{ marginBottom: 10 }}>
            <b>{m.user}:</b> {m.text}

            {m.user === username && (
              <>
                <button
                  onClick={() => {
                    const newText = prompt("Neue Nachricht:", m.text);
                    if (!newText) return;
                    socket.emit("editMessage", { channel, index: i, newText });
                  }}
                  style={{ marginLeft: 10 }}
                >
                  ‚úèÔ∏è
                </button>

                <button
                  onClick={() => {
                    if (!window.confirm("Nachricht l√∂schen?")) return;
                    socket.emit("deleteMessage", { channel, index: i });
                  }}
                  style={{ marginLeft: 5 }}
                >
                  üóëÔ∏è
                </button>
              </>
            )}
          </div>
        ))}
      </div>

      <div
        style={{
          display: "flex",
          padding: 10,
          background: "#232428",
          borderTop: "1px solid #3a3b3f"
        }}
      >
        <input
          id="chatInput"
          onKeyDown={handleKey}
          placeholder={`Nachricht in #${channel}...`}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 4,
            border: "none",
            outline: "none"
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            marginLeft: 10,
            padding: "10px 20px",
            background: "#5865F2",
            border: "none",
            borderRadius: 4,
            color: "white",
            cursor: "pointer"
          }}
        >
          Senden
        </button>
      </div>
    </div>
  );
}
