import React, { useState, useEffect } from "react";
import { socket } from "../socket";

export default function UserList() {
  const [voiceState, setVoiceState] = useState({});
  const [speakingUsers, setSpeakingUsers] = useState([]);

  useEffect(() => {
    socket.on("voiceStateUpdate", setVoiceState);

    socket.on("speakingStart", ({ user }) => {
      setSpeakingUsers(prev => [...new Set([...prev, user])]);
    });

    socket.on("speakingStop", ({ user }) => {
      setSpeakingUsers(prev => prev.filter(u => u !== user));
    });
  }, []);

  return (
    <div style={{
      width: "100%",
      height: "100%",
      background: "#2b2d31",
      padding: 10,
      color: "white"
    }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>VOICE USERS</div>

      {Object.values(voiceState).map(user => (
        <div key={user.username} style={{
          display: "flex",
          alignItems: "center",
          marginBottom: 8
        }}>
          <div className={speakingUsers.includes(user.username) ? "speaking-ring" : ""} style={{
            width: 32,
            height: 32,
            borderRadius: "50%",
            overflow: "hidden",
            marginRight: 8
          }}>
            <img src="/avatar.png" className="avatar" alt="" />
          </div>

          <div>{user.username}</div>
        </div>
      ))}
    </div>
  );
}
