import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function ChannelList({ selectedChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState([]);
  const [voiceChannels, setVoiceChannels] = useState({});
  const [menu, setMenu] = useState(null); // {x, y, channel}

  useEffect(() => {
    // Text-Channels kommen NUR aus channelListUpdate
    socket.on("channelListUpdate", ({ textChannels }) => {
      setTextChannels(textChannels);
    });

    // Voice-Channels kommen NUR aus voiceChannelUpdate
    socket.on("voiceChannelUpdate", (voiceChannels) => {
      setVoiceChannels(voiceChannels);
    });

    socket.emit("requestChannelList");

    const closeMenu = () => setMenu(null);
    window.addEventListener("click", closeMenu);

    return () => {
      socket.off("channelListUpdate");
      socket.off("voiceChannelUpdate");
      window.removeEventListener("click", closeMenu);
    };
  }, []);

  const createChannel = () => {
    const name = prompt("Neuer Channelname:");
    if (!name) return;
    socket.emit("createChannel", { name });
  };

  const renameChannel = (oldName) => {
    const newName = prompt("Neuer Name:", oldName);
    if (!newName) return;
    socket.emit("renameChannel", { oldName, newName });
  };

  const deleteChannel = (name) => {
    if (!window.confirm(`Channel "${name}" lÃ¶schen?`)) return;
    socket.emit("deleteChannel", { name });
  };

  return (
    <div style={{ padding: 10, color: "white", position: "relative" }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>TEXT CHANNELS</div>

      <button
        onClick={createChannel}
        style={{
          width: "100%",
          padding: 6,
          background: "#5865F2",
          border: "none",
          borderRadius: 4,
          color: "white",
          cursor: "pointer",
          marginBottom: 10
        }}
      >
        + Channel hinzufÃ¼gen
      </button>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          onContextMenu={(e) => {
            e.preventDefault();
            setMenu({ x: e.clientX, y: e.clientY, channel: ch });
          }}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            background: selectedChannel === ch ? "#3a3b3f" : "transparent",
            borderRadius: 4,
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ opacity: 0.6, marginTop: 20, marginBottom: 10 }}>
        VOICE CHANNELS
      </div>

      {Object.keys(voiceChannels).map((ch) => (
        <div
          key={ch}
          onClick={() => {
            const username = localStorage.getItem("username");
            socket.emit("joinVoiceChannel", { channel: ch, username });
          }}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            borderRadius: 4,
            marginBottom: 4,
            background: "#2f3136"
          }}
        >
          ðŸ”Š {ch}
        </div>
      ))}

      {menu && (
        <div
          className="context-menu"
          style={{ top: menu.y, left: menu.x }}
        >
          <div
            className="context-menu-item"
            onClick={() => renameChannel(menu.channel)}
          >
            Umbenennen
          </div>
          <div
            className="context-menu-item"
            onClick={() => deleteChannel(menu.channel)}
          >
            LÃ¶schen
          </div>
        </div>
      )}
    </div>
  );
}
