import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function ChannelList({ selectedChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState([]);

  useEffect(() => {
    socket.on("channelListUpdate", ({ textChannels }) => {
      setTextChannels(textChannels);
    });

    // Initiale Liste anfordern
    socket.emit("requestChannelList");

    return () => socket.off("channelListUpdate");
  }, []);

  const createChannel = () => {
    const name = prompt("Neuer Channelname:");
    if (!name) return;
    socket.emit("createChannel", { name });
  };

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>TEXT CHANNELS</div>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          style={{
            padding: "6px 10px",
            cursor: "pointer",
            background: selectedChannel === ch ? "#3a3b3f" : "transparent",
            borderRadius: 4,
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <button
        onClick={createChannel}
        style={{
          marginTop: 10,
          width: "100%",
          padding: 6,
          background: "#5865F2",
          border: "none",
          borderRadius: 4,
          color: "white",
          cursor: "pointer"
        }}
      >
        + Channel hinzuf√ºgen
      </button>
    </div>
  );
}
