import React, { useState, useEffect } from "react";
import {
  joinVoiceChannel,
  leaveVoiceChannel,
  muteMic,
  unmuteMic,
} from "../webrtc";
import { socket } from "../socket";

function VoicePanel({ channel }) {
  const [inVoice, setInVoice] = useState(false);
  const [voiceUsers, setVoiceUsers] = useState([]);
  const [speakingUsers, setSpeakingUsers] = useState({});
  const [muted, setMuted] = useState(false);
  const [pttEnabled, setPttEnabled] = useState(false);

  useEffect(() => {
    socket.on("voice:user-joined", ({ id, channel: ch }) => {
      if (ch !== channel) return;
      setVoiceUsers((prev) => [...prev, id]);
    });

    socket.on("voice:user-left", ({ id }) => {
      setVoiceUsers((prev) => prev.filter((u) => u !== id));
      setSpeakingUsers((prev) => {
        const copy = { ...prev };
        delete copy[id];
        return copy;
      });
    });

    socket.on("voice:speaking", ({ id, speaking }) => {
      setSpeakingUsers((prev) => ({ ...prev, [id]: speaking }));
    });

    return () => {
      socket.off("voice:user-joined");
      socket.off("voice:user-left");
      socket.off("voice:speaking");
    };
  }, [channel]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!pttEnabled) return;
      if (e.code === "Space") {
        unmuteMic();
        setMuted(false);
      }
    };

    const handleKeyUp = (e) => {
      if (!pttEnabled) return;
      if (e.code === "Space") {
        muteMic();
        setMuted(true);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [pttEnabled]);

  const join = async () => {
    await joinVoiceChannel(channel);
    setInVoice(true);
    setMuted(false);
  };

  const leave = () => {
    leaveVoiceChannel();
    setInVoice(false);
    setVoiceUsers([]);
    setSpeakingUsers({});
    setMuted(false);
  };

  const toggleMute = () => {
    if (!inVoice) return;
    if (muted) {
      unmuteMic();
      setMuted(false);
    } else {
      muteMic();
      setMuted(true);
    }
  };

  const setVolume = (id, value) => {
    const audio = document.getElementById("audio-" + id);
    if (audio) audio.volume = value;
  };

  return (
    <div style={{
      width: 240,
      background: "#1e1f22",
      color: "#fff",
      padding: 16,
      borderLeft: "1px solid #2a2b2f",
      display: "flex",
      flexDirection: "column",
      gap: 16
    }}>
      <div style={{ fontSize: 14, fontWeight: "bold", opacity: 0.8 }}>
        Voice â€“ #{channel}
      </div>

      {!inVoice ? (
        <button
          onClick={join}
          style={{
            padding: "8px 12px",
            background: "#5865f2",
            border: "none",
            borderRadius: 4,
            color: "#fff",
            cursor: "pointer",
            fontWeight: "bold"
          }}
        >
          Voice beitreten
        </button>
      ) : (
        <button
          onClick={leave}
          style={{
            padding: "8px 12px",
            background: "#d83c3e",
            border: "none",
            borderRadius: 4,
            color: "#fff",
            cursor: "pointer",
            fontWeight: "bold"
          }}
        >
          Voice verlassen
        </button>
      )}

      <button
        onClick={toggleMute}
        disabled={!inVoice}
        style={{
          padding: "8px 12px",
          background: muted ? "#3a3c43" : "#5865f2",
          border: "none",
          borderRadius: 4,
          color: "#fff",
          cursor: "pointer",
          fontWeight: "bold"
        }}
      >
        {muted ? "ðŸ”‡ Stumm" : "ðŸŽ¤ Aktiv"}
      </button>

      <label style={{ fontSize: 13, opacity: 0.8 }}>
        <input
          type="checkbox"
          checked={pttEnabled}
          onChange={(e) => setPttEnabled(e.target.checked)}
          style={{ marginRight: 6 }}
        />
        Pushâ€‘toâ€‘Talk (Leertaste)
      </label>

      <div style={{ marginTop: 10, fontSize: 13, opacity: 0.7 }}>
        Voiceâ€‘Teilnehmer
      </div>

      <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>
        {voiceUsers.map((id) => (
          <div
            key={id}
            style={{
              padding: 8,
              borderRadius: 6,
              background: "#2b2d31",
              border: speakingUsers[id]
                ? "2px solid #43b581"
                : "2px solid transparent",
              display: "flex",
              flexDirection: "column",
              gap: 6
            }}
          >
            <audio id={"audio-" + id} autoPlay />
            <div style={{ fontSize: 12, opacity: 0.8 }}>User: {id}</div>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              defaultValue="1"
              onChange={(e) => setVolume(id, e.target.value)}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default VoicePanel;
