import React, { useEffect } from "react";
import ChatPage from "./pages/ChatPage";

function App() {
  useEffect(() => {
    let name = localStorage.getItem("username");

    while (!name || name.trim() === "") {
      name = prompt("Bitte gib deinen Namen ein:");
    }

    localStorage.setItem("username", name);
  }, []);

  return <ChatPage />;
}

export default App;
