import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function UserList() {
  const [voiceChannels, setVoiceChannels] = useState({});
  const [users, setUsers] = useState({});
  const [speakingUsers, setSpeakingUsers] = useState({});

  useEffect(() => {
    socket.on("voiceChannelUpdate", setVoiceChannels);
    socket.on("avatarUpdate", setUsers);

    socket.on("speakingStart", ({ user }) =>
      setSpeakingUsers((prev) => ({ ...prev, [user]: true }))
    );

    socket.on("speakingStop", ({ user }) =>
      setSpeakingUsers((prev) => ({ ...prev, [user]: false }))
    );

    return () => {
      socket.off("voiceChannelUpdate");
      socket.off("avatarUpdate");
      socket.off("speakingStart");
      socket.off("speakingStop");
    };
  }, []);

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>VOICE USERS</div>

      {Object.entries(voiceChannels).map(([channel, list]) => (
        <div key={channel} style={{ marginBottom: 15 }}>
          <div style={{ fontSize: 12, opacity: 0.7, marginBottom: 4 }}>
            {channel}
          </div>

          {list.map((u) => {
            const av = users[u.username] || {
              avatarType: "default",
              avatarColor: "#5865F2",
              avatarLetter: u.username[0]?.toUpperCase() || "?",
              avatarImage: null
            };

            const isSpeaking = speakingUsers[u.username];

            return (
              <div
                key={u.id}
                style={{
                  display: "flex",
                  alignItems: "center",
                  marginBottom: 4
                }}
              >
                <div
                  className={isSpeaking ? "speaking-ring" : undefined}
                  style={{
                    width: 28,
                    height: 28,
                    borderRadius: "50%",
                    overflow: "hidden",
                    marginRight: 8,
                    background:
                      av.avatarType === "default"
                        ? av.avatarColor
                        : "transparent",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontWeight: "bold",
                    fontSize: 14
                  }}
                >
                  {av.avatarType === "image" ? (
                    <img src={av.avatarImage} className="avatar" alt="" />
                  ) : (
                    av.avatarLetter
                  )}
                </div>

                <div>{u.username}</div>
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
}
