// src/components/ChatWindow.jsx
import React, { useEffect, useState } from "react";
import socket from "../socket";

const API_BASE = "http://localhost:3000";

function ChatWindow({ currentChannel, username }) {
  const [messages, setMessages] = useState([]);
  const [contextMessageId, setContextMessageId] = useState(null);
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editingText, setEditingText] = useState("");

  async function loadMessages(channel) {
    if (!channel) return;
    try {
      const res = await fetch(`${API_BASE}/api/messages/list/${encodeURIComponent(channel)}`);
      const data = await res.json();
      if (data.ok) {
        setMessages(data.messages);
      }
    } catch (err) {
      console.error("Fehler beim Laden der Nachrichten:", err);
    }
  }

  useEffect(() => {
    function handleNewMessage(msg) {
      if (msg.channel === currentChannel) {
        setMessages((prev) => [...prev, msg]);
      }
    }

    function handleMessageEdited(msg) {
      setMessages((prev) =>
        prev.map((m) => (m.id === msg.id ? { ...m, text: msg.text } : m))
      );
    }

    function handleMessageDeleted({ messageId }) {
      setMessages((prev) => prev.filter((m) => m.id !== messageId));
    }

    socket.on("newMessage", handleNewMessage);
    socket.on("messageEdited", handleMessageEdited);
    socket.on("messageDeleted", handleMessageDeleted);

    return () => {
      socket.off("newMessage", handleNewMessage);
      socket.off("messageEdited", handleMessageEdited);
      socket.off("messageDeleted", handleMessageDeleted);
    };
  }, [currentChannel]);

  useEffect(() => {
    if (currentChannel) {
      loadMessages(currentChannel);
    }
  }, [currentChannel]);

  function handleSend(text) {
    if (!currentChannel || !username || !text.trim()) return;
    socket.emit("sendMessage", {
      channel: currentChannel,
      user: username,
      text: text.trim()
    });
  }

  async function handleDelete(messageId) {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Keine Session – bitte neu einloggen.");
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/messages/delete`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-session-id": sessionId
        },
        body: JSON.stringify({ messageId })
      });

      const data = await res.json();
      if (!data.ok) {
        alert(data.error || "Nachricht konnte nicht gelöscht werden.");
      }
    } catch (err) {
      console.error("Fehler beim Löschen der Nachricht:", err);
    }
  }

  async function handleEditSubmit(e) {
    e.preventDefault();
    if (!editingMessageId || !editingText.trim()) return;

    const sessionId = localStorage.getItem("sessionId");

    try {
      const res = await fetch(`${API_BASE}/api/messages/edit`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-session-id": sessionId
        },
        body: JSON.stringify({
          messageId: editingMessageId,
          newText: editingText.trim()
        })
      });

      const data = await res.json();
      if (!data.ok) {
        alert(data.error || "Nachricht konnte nicht bearbeitet werden.");
        return;
      }

      setEditingMessageId(null);
      setEditingText("");
    } catch (err) {
      console.error("Fehler beim Bearbeiten der Nachricht:", err);
    }
  }

  return (
    <div className="chat-window">
      <div className="chat-header">
        {currentChannel ? `#${currentChannel}` : "Kein Channel ausgewählt"}
      </div>

      <div className="chat-messages">
        {messages.map((msg) => (
          <div
            key={msg.id}
            className="chat-message"
            onContextMenu={(e) => {
              e.preventDefault();
              setContextMessageId(msg.id);
            }}
          >
            <div className="chat-message-meta">
              <span className="chat-message-user">{msg.user}</span>
              <span className="chat-message-time">
                {new Date(msg.time).toLocaleTimeString()}
              </span>
            </div>

            <div className="chat-message-text">
              {editingMessageId === msg.id ? (
                <form onSubmit={handleEditSubmit}>
                  <input
                    type="text"
                    value={editingText}
                    onChange={(e) => setEditingText(e.target.value)}
                    autoFocus
                  />
                </form>
              ) : (
                msg.text
              )}
            </div>

            {contextMessageId === msg.id && (
              <div className="chat-message-context-menu">
                <button
                  onClick={() => {
                    setEditingMessageId(msg.id);
                    setEditingText(msg.text);
                    setContextMessageId(null);
                  }}
                >
                  Bearbeiten
                </button>
                <button
                  onClick={() => {
                    handleDelete(msg.id);
                    setContextMessageId(null);
                  }}
                >
                  Löschen
                </button>
                <button onClick={() => setContextMessageId(null)}>Schließen</button>
              </div>
            )}
          </div>
        ))}
      </div>

      <ChatInput onSend={handleSend} />
    </div>
  );
}

function ChatInput({ onSend }) {
  const [value, setValue] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    if (!value.trim()) return;
    onSend(value);
    setValue("");
  }

  return (
    <form className="chat-input" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Nachricht eingeben..."
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <button type="submit">Senden</button>
    </form>
  );
}

export default ChatWindow;
