import React, { useState, useEffect, useRef } from "react";
import { socket } from "../socket";
import { startCall, stopCall } from "../webrtc";

export default function VoicePanel() {
  const [inCall, setInCall] = useState(false);
  const [muted, setMuted] = useState(false);
  const [speaking, setSpeaking] = useState(false);

  // Lokaler Audiostream (f√ºr Mute/Stop)
  const streamRef = useRef(null);

  // Speaking-Events empfangen
  useEffect(() => {
    const handleStart = ({ user }) => {
      if (user === (localStorage.getItem("username") || "Unbekannt")) {
        setSpeaking(true);
      }
    };

    const handleStop = ({ user }) => {
      if (user === (localStorage.getItem("username") || "Unbekannt")) {
        setSpeaking(false);
      }
    };

    socket.on("speakingStart", handleStart);
    socket.on("speakingStop", handleStop);

    return () => {
      socket.off("speakingStart", handleStart);
      socket.off("speakingStop", handleStop);
    };
  }, []);

  // Voice starten
  const startVoice = async () => {
    if (inCall) return;

    const stream = await startCall();
    streamRef.current = stream;

    setMuted(false);
    setInCall(true);
  };

  // Voice stoppen
  const stopVoice = () => {
    stopCall();

    if (streamRef.current) {
      streamRef.current.getTracks().forEach((t) => t.stop());
      streamRef.current = null;
    }

    setMuted(false);
    setSpeaking(false);
    setInCall(false);
  };

  // Mute toggeln
  const toggleMute = () => {
    if (!streamRef.current) return;

    const tracks = streamRef.current.getAudioTracks();
    if (!tracks.length) return;

    const enabled = tracks[0].enabled;
    tracks.forEach((t) => (t.enabled = !enabled));

    setMuted(enabled);
  };

  return (
    <div
      style={{
        height: "90px",
        background: "#232428",
        display: "flex",
        alignItems: "center",
        padding: "10px",
        color: "white"
      }}
    >
      <div
        className={speaking ? "speaking-ring" : ""}
        style={{
          width: 40,
          height: 40,
          borderRadius: "50%",
          overflow: "hidden",
          marginRight: 10
        }}
      >
        <img src="/avatar.png" className="avatar" alt="" />
      </div>

      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: "bold" }}>
          {localStorage.getItem("username") || "Unbekannt"}
        </div>

        <div style={{ fontSize: 12, color: "#b9bbbe" }}>
          {!inCall
            ? "Nicht verbunden"
            : muted
            ? "Stummgeschaltet"
            : speaking
            ? "Spricht..."
            : "Verbunden"}
        </div>
      </div>

      {!inCall && (
        <button onClick={startVoice} style={btnStyle}>
          üìû
        </button>
      )}

      {inCall && (
        <>
          <button onClick={toggleMute} style={btnStyle}>
            {muted ? "üîá" : "üé§"}
          </button>

          <button onClick={stopVoice} style={btnStyle}>
            ‚ùå
          </button>
        </>
      )}
    </div>
  );
}

const btnStyle = {
  background: "transparent",
  border: "none",
  color: "white",
  fontSize: 20,
  cursor: "pointer",
  marginLeft: 8
};
