import React, { useState, useEffect } from "react";
import { socket } from "../socket";

export default function ChannelList({ currentChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState(["admin", "general", "gaming", "music"]);
  const [voiceChannels, setVoiceChannels] = useState(["admin", "general", "gaming", "music"]);

  useEffect(() => {
    socket.on("channelListUpdate", ({ textChannels, voiceChannels }) => {
      setTextChannels(textChannels);
      setVoiceChannels(Object.keys(voiceChannels));
    });
  }, []);

  const createChannel = () => {
    const name = prompt("Neuer Channelname:");
    if (name) socket.emit("createChannel", { name });
  };

  const renameChannel = (oldName) => {
    const newName = prompt("Neuer Name:");
    if (newName) socket.emit("renameChannel", { oldName, newName });
  };

  const deleteChannel = (name) => {
    if (window.confirm("Channel lÃ¶schen?")) {
      socket.emit("deleteChannel", { name });
    }
  };

  const joinVoice = (name) => {
    const channel = name.toLowerCase();
    localStorage.setItem("voiceChannel", channel);
    socket.emit("joinVoiceChannel", {
      channel,
      username: localStorage.getItem("username")
    });
  };

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div
        onClick={createChannel}
        style={{
          padding: "6px 10px",
          borderRadius: 4,
          background: "#3a3c41",
          cursor: "pointer",
          marginBottom: 10,
          textAlign: "center"
        }}
      >
        + Channel
      </div>

      <div style={{ marginBottom: 10, opacity: 0.6 }}>TEXT CHANNELS</div>

      {textChannels.map(ch => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          onContextMenu={(e) => {
            e.preventDefault();
            const action = prompt("1 = Umbenennen, 2 = LÃ¶schen");
            if (action === "1") renameChannel(ch);
            if (action === "2") deleteChannel(ch);
          }}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            background: currentChannel === ch ? "#3a3c41" : "transparent",
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ marginTop: 20, marginBottom: 10, opacity: 0.6 }}>VOICE CHANNELS</div>

      {voiceChannels.map(vc => (
        <div
          key={vc}
          onClick={() => joinVoice(vc)}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          ðŸ”Š {vc}
        </div>
      ))}
    </div>
  );
}
