const express = require("express");
const http = require("http");
const cors = require("cors");
const { Server } = require("socket.io");

const app = express();
app.use(cors());
app.use(express.json({ limit: "20mb" }));

const server = http.createServer(app);

const io = new Server(server, {
  cors: { origin: "*", methods: ["GET", "POST"] }
});

// ---------------- DEFAULT CHANNELS ----------------
let textChannels = ["admin", "general", "gaming", "music"];

let voiceChannels = {
  admin: [],
  general: [],
  gaming: [],
  music: []
};

// ---------------- USERS / AVATARE ----------------
let users = {};
let socketUsers = {}; // socket.id -> username

function defaultAvatar(username) {
  const colors = ["#ff8800", "#5865F2", "#57F287", "#FEE75C", "#EB459E"];
  return {
    avatarType: "default",
    avatarColor: colors[username.charCodeAt(0) % colors.length],
    avatarLetter: username[0].toUpperCase(),
    avatarImage: null
  };
}

// ---------------- NACHRICHTEN ----------------
let messages = {
  admin: [],
  general: [],
  gaming: [],
  music: []
};

// ---------------- SOCKET.IO ----------------
io.on("connection", (socket) => {
  console.log("connected:", socket.id);

  // USER REGISTRIEREN
  socket.on("registerUser", ({ username }) => {
    socketUsers[socket.id] = username;
    if (!users[username]) users[username] = defaultAvatar(username);
    io.emit("avatarUpdate", users);
  });

  // AVATAR UPDATE
  socket.on("updateAvatar", (data) => {
    users[data.username] = data;
    io.emit("avatarUpdate", users);
  });

  // CHANNEL LISTE SENDEN (Start-Request)
  socket.on("requestChannelList", () => {
    socket.emit("channelListUpdate", { textChannels });
    socket.emit("voiceChannelUpdate", voiceChannels);
  });

  // CHANNEL ERSTELLEN
  socket.on("createChannel", ({ name }) => {
    if (!name) return;
    if (!textChannels.includes(name)) {
      textChannels.push(name);
      voiceChannels[name] = [];
      messages[name] = [];

      io.emit("channelListUpdate", { textChannels });
      io.emit("voiceChannelUpdate", voiceChannels);
    }
  });

  // CHANNEL UMBENENNEN
  socket.on("renameChannel", ({ oldName, newName }) => {
    if (!textChannels.includes(oldName)) return;
    if (textChannels.includes(newName)) return;

    textChannels = textChannels.map((c) => (c === oldName ? newName : c));

    messages[newName] = messages[oldName];
    delete messages[oldName];

    voiceChannels[newName] = voiceChannels[oldName];
    delete voiceChannels[oldName];

    io.emit("channelListUpdate", { textChannels });
    io.emit("voiceChannelUpdate", voiceChannels);
  });

  // CHANNEL LÖSCHEN
  socket.on("deleteChannel", ({ name }) => {
    textChannels = textChannels.filter((c) => c !== name);
    delete messages[name];
    delete voiceChannels[name];

    io.emit("channelListUpdate", { textChannels });
    io.emit("voiceChannelUpdate", voiceChannels);
  });

  // NACHRICHT SENDEN
  socket.on("sendMessage", ({ channel, user, text }) => {
    const msg = { channel, user, text, time: Date.now() };
    if (!messages[channel]) messages[channel] = [];
    messages[channel].push(msg);
    io.emit("newMessage", msg);
  });

  // NACHRICHTEN LADEN
  socket.on("requestMessages", ({ channel }) => {
    socket.emit("loadMessages", messages[channel] || []);
  });

  // NACHRICHT BEARBEITEN
  socket.on("editMessage", ({ channel, index, newText }) => {
    if (!messages[channel] || !messages[channel][index]) return;
    messages[channel][index].text = newText;
    io.emit("loadMessages", messages[channel]);
  });

  // NACHRICHT LÖSCHEN
  socket.on("deleteMessage", ({ channel, index }) => {
    if (!messages[channel]) return;
    messages[channel].splice(index, 1);
    io.emit("loadMessages", messages[channel]);
  });

  // ---------------- VOICE JOIN ----------------
  socket.on("joinVoiceChannel", ({ channel }) => {
    const username = socketUsers[socket.id];
    if (!username) return;

    // User anhand USERNAME und SOCKET-ID aus allen Channels entfernen
    for (const ch in voiceChannels) {
      voiceChannels[ch] = voiceChannels[ch].filter(
        (u) => u.username !== username && u.id !== socket.id
      );
      socket.leave("vc-" + ch);
    }

    if (!voiceChannels[channel]) voiceChannels[channel] = [];
    voiceChannels[channel].push({ id: socket.id, username });

    socket.join("vc-" + channel);

    io.emit("voiceChannelUpdate", voiceChannels);
  });

  // ---------------- VOICE LEAVE (Disconnect-Button) ----------------
  socket.on("leaveVoiceChannel", ({ channel }) => {
    const username = socketUsers[socket.id];

    if (voiceChannels[channel]) {
      voiceChannels[channel] = voiceChannels[channel].filter(
        (u) => u.username !== username && u.id !== socket.id
      );
    }

    socket.leave("vc-" + channel);
    io.emit("voiceChannelUpdate", voiceChannels);
  });

  // ---------------- SPEAKING EVENTS ----------------
  socket.on("speakingStart", ({ channel }) => {
    const username = socketUsers[socket.id];
    io.to("vc-" + channel).emit("speakingStart", { user: username });
  });

  socket.on("speakingStop", ({ channel }) => {
    const username = socketUsers[socket.id];
    io.to("vc-" + channel).emit("speakingStop", { user: username });
  });

  // ---------------- DISCONNECT ----------------
  socket.on("disconnect", () => {
    const username = socketUsers[socket.id];

    for (const ch in voiceChannels) {
      voiceChannels[ch] = voiceChannels[ch].filter(
        (u) => u.username !== username && u.id !== socket.id
      );
    }

    delete socketUsers[socket.id];

    io.emit("voiceChannelUpdate", voiceChannels);
  });
});

server.listen(3000, () => console.log("Server läuft auf Port 3000"));
