import React, { useState, useEffect } from "react";
import { socket } from "../socket";

export default function UserList() {
  const [voiceUsers, setVoiceUsers] = useState([]);

  useEffect(() => {
    const handleVoiceUpdate = (voiceChannels) => {
      const list = [];

      Object.entries(voiceChannels).forEach(([channel, users]) => {
        users.forEach((u) => {
          list.push({
            username: u.username,
            channel
          });
        });
      });

      setVoiceUsers(list);
    };

    socket.on("voiceChannelUpdate", handleVoiceUpdate);
    return () => socket.off("voiceChannelUpdate", handleVoiceUpdate);
  }, []);

  return (
    <div
      style={{
        width: "100%",
        height: "100%",
        background: "#2b2d31",
        padding: 10,
        color: "white"
      }}
    >
      <div style={{ opacity: 0.6, marginBottom: 10 }}>VOICE USERS</div>

      {voiceUsers.map((u, i) => (
        <div
          key={`${u.username}-${u.channel}-${i}`}
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: 8
          }}
        >
          <div
            style={{
              width: 32,
              height: 32,
              borderRadius: "50%",
              overflow: "hidden",
              marginRight: 8
            }}
          >
            <img src="/avatar.png" className="avatar" alt="" />
          </div>

          <div>
            {u.username} ist im Voice Channel {u.channel}
          </div>
        </div>
      ))}

      {voiceUsers.length === 0 && (
        <div style={{ fontSize: 12, opacity: 0.6 }}>Niemand ist in einem Voice Channel</div>
      )}
    </div>
  );
}
