import React, { useEffect, useState, useRef } from "react";
import { socket } from "../socket";

export default function ChatWindow({ channel }) {
  const [messages, setMessages] = useState([]);
  const [contextMenu, setContextMenu] = useState(null);
  const username = localStorage.getItem("username");
  const scrollRef = useRef(null);

  useEffect(() => {
    socket.emit("requestMessages", { channel });
  }, [channel]);

  useEffect(() => {
    const load = (msgs) => setMessages(msgs);
    const add = (msg) => {
      if (msg.channel === channel) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    const edit = ({ index, newText }) => {
      setMessages((prev) => {
        const copy = [...prev];
        if (copy[index]) copy[index].text = newText;
        return copy;
      });
    };
    const del = ({ index }) => {
      setMessages((prev) => prev.filter((_, i) => i !== index));
    };

    socket.on("loadMessages", load);
    socket.on("newMessage", add);
    socket.on("messageEdited", edit);
    socket.on("messageDeleted", del);

    return () => {
      socket.off("loadMessages", load);
      socket.off("newMessage", add);
      socket.off("messageEdited", edit);
      socket.off("messageDeleted", del);
    };
  }, [channel]);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    const input = document.getElementById("chatInput");
    const text = input.value.trim();
    if (!text) return;

    socket.emit("sendMessage", {
      channel,
      user: username,
      text
    });

    input.value = "";
  };

  const handleKey = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  const openContextMenu = (e, index) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      index
    });
  };

  const closeContextMenu = () => setContextMenu(null);

  const editMessage = (index) => {
    const newText = prompt("Neue Nachricht:");
    if (!newText) return;
    socket.emit("editMessage", { channel, index, newText });
  };

  const deleteMessage = (index) => {
    if (!window.confirm("Nachricht löschen?")) return;
    socket.emit("deleteMessage", { channel, index });
  };

  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column", height: "100%" }}>
      <div
        ref={scrollRef}
        style={{
          flex: 1,
          overflowY: "auto",
          padding: 20,
          color: "white"
        }}
      >
        <h3># {channel}</h3>

        {messages.map((m, i) => (
          <div
            key={i}
            onContextMenu={(e) => openContextMenu(e, i)}
            style={{ marginBottom: 10, cursor: "context-menu" }}
          >
            <b>{m.user}:</b> {m.text}
          </div>
        ))}
      </div>

      <div
        style={{
          display: "flex",
          padding: 10,
          background: "#232428",
          borderTop: "1px solid #3a3b3f"
        }}
      >
        <input
          id="chatInput"
          onKeyDown={handleKey}
          placeholder={`Nachricht in #${channel}...`}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 4,
            border: "none",
            outline: "none"
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            marginLeft: 10,
            padding: "10px 20px",
            background: "#5865F2",
            border: "none",
            borderRadius: 4,
            color: "white",
            cursor: "pointer"
          }}
        >
          Senden
        </button>
      </div>

      {contextMenu && (
        <div
          style={{
            position: "fixed",
            top: contextMenu.y,
            left: contextMenu.x,
            background: "#2b2d31",
            padding: 10,
            borderRadius: 4,
            zIndex: 999,
            boxShadow: "0 0 10px rgba(0,0,0,0.5)"
          }}
          onMouseLeave={closeContextMenu}
        >
          <div
            style={{ padding: 5, cursor: "pointer" }}
            onClick={() => {
              editMessage(contextMenu.index);
              closeContextMenu();
            }}
          >
            Bearbeiten
          </div>
          <div
            style={{ padding: 5, cursor: "pointer", color: "red" }}
            onClick={() => {
              deleteMessage(contextMenu.index);
              closeContextMenu();
            }}
          >
            Löschen
          </div>
        </div>
      )}
    </div>
  );
}
