import React, { useEffect, useRef, useState } from "react";
import { socket } from "../socket";
import {
  startCall,
  handleOffer,
  handleAnswer,
  handleIceCandidate,
  endCall,
  muteLocalAudio,
  unmuteLocalAudio,
  isLocalMuted,
} from "../webrtc";

const PTT_KEY = "v";

function VoicePanel() {
  const remoteAudioRef = useRef(null);
  const [inCall, setInCall] = useState(false);
  const [muted, setMuted] = useState(false);
  const [pushToTalkEnabled, setPushToTalkEnabled] = useState(false);
  const [pttActive, setPttActive] = useState(false);

  useEffect(() => {
    socket.on("webrtc-offer", async (offer) => {
      await handleOffer(offer, (remoteStream) => {
        remoteAudioRef.current.srcObject = remoteStream;
      });
      setInCall(true);
      setMuted(isLocalMuted());
    });

    socket.on("webrtc-answer", async (answer) => {
      await handleAnswer(answer);
    });

    socket.on("webrtc-ice-candidate", (candidate) => {
      handleIceCandidate(candidate);
    });

    return () => {
      socket.off("webrtc-offer");
      socket.off("webrtc-answer");
      socket.off("webrtc-ice-candidate");
    };
  }, []);

  useEffect(() => {
    if (!pushToTalkEnabled) return;

    const down = (e) => {
      if (e.key.toLowerCase() === PTT_KEY && !pttActive) {
        setPttActive(true);
        unmuteLocalAudio();
        setMuted(false);
      }
    };

    const up = (e) => {
      if (e.key.toLowerCase() === PTT_KEY && pttActive) {
        setPttActive(false);
        muteLocalAudio();
        setMuted(true);
      }
    };

    window.addEventListener("keydown", down);
    window.addEventListener("keyup", up);

    return () => {
      window.removeEventListener("keydown", down);
      window.removeEventListener("keyup", up);
    };
  }, [pushToTalkEnabled, pttActive]);

  const startVoiceCall = async () => {
    await startCall((remoteStream) => {
      remoteAudioRef.current.srcObject = remoteStream;
    });
    setInCall(true);
    setMuted(isLocalMuted());
  };

  const stopVoiceCall = () => {
    endCall();
    setInCall(false);
    setMuted(false);
    setPttActive(false);
  };

  const toggleMute = () => {
    if (isLocalMuted()) {
      unmuteLocalAudio();
      setMuted(false);
    } else {
      muteLocalAudio();
      setMuted(true);
    }
  };

  const togglePTT = () => {
    const next = !pushToTalkEnabled;
    setPushToTalkEnabled(next);

    if (next) {
      muteLocalAudio();
      setMuted(true);
      setPttActive(false);
    }
  };

  return (
    <div style={{
      display: "flex",
      alignItems: "center",
      height: "100%",
      padding: "10px",
      color: "white"
    }}>
      
      {/* Avatar */}
      <div style={{
        width: 40,
        height: 40,
        borderRadius: "50%",
        background: "#7289da",
        marginRight: 10
      }} />

      {/* Username + Status */}
      <div style={{ flex: 1 }}>
        <div style={{ fontSize: 14, fontWeight: "bold" }}>
          {localStorage.getItem("username")}
        </div>
        <div style={{ fontSize: 12, color: "#bbb" }}>
          {muted ? "Muted" : pttActive ? "Speaking..." : "Connected"}
        </div>
      </div>

      {/* Buttons */}
      {inCall && (
        <>
          <button onClick={toggleMute} style={{
            marginRight: 5,
            background: "transparent",
            border: "none",
            color: muted ? "#f04747" : "#43b581",
            fontSize: 18,
            cursor: "pointer"
          }}>
            {muted ? "üîá" : "üé§"}
          </button>

          <button onClick={togglePTT} style={{
            marginRight: 5,
            background: "transparent",
            border: "none",
            color: pushToTalkEnabled ? "#7289da" : "#bbb",
            fontSize: 18,
            cursor: "pointer"
          }}>
            ‚å®Ô∏è
          </button>

          <button onClick={stopVoiceCall} style={{
            background: "transparent",
            border: "none",
            color: "#f04747",
            fontSize: 18,
            cursor: "pointer"
          }}>
            ‚ùå
          </button>
        </>
      )}

      {!inCall && (
        <button onClick={startVoiceCall} style={{
          background: "transparent",
          border: "none",
          color: "#43b581",
          fontSize: 18,
          cursor: "pointer"
        }}>
          üìû
        </button>
      )}

      <audio ref={remoteAudioRef} autoPlay />
    </div>
  );
}

export default VoicePanel;
