import React, { useState, useEffect, useRef } from "react";
import { socket } from "../socket";
import { startCall, stopCall } from "../webrtc";

export default function VoicePanel() {
  const [inCall, setInCall] = useState(false);
  const [muted, setMuted] = useState(false);
  const [speaking, setSpeaking] = useState(false);
  const [voiceChannel, setVoiceChannel] = useState(null);

  const streamRef = useRef(null);
  const inCallRef = useRef(false);

  const username = localStorage.getItem("username") || "Speedy";

  // Speaking-Events
  useEffect(() => {
    const handleStart = ({ user }) => {
      if (user === username) setSpeaking(true);
    };
    const handleStop = ({ user }) => {
      if (user === username) setSpeaking(false);
    };

    socket.on("speakingStart", handleStart);
    socket.on("speakingStop", handleStop);

    return () => {
      socket.off("speakingStart", handleStart);
      socket.off("speakingStop", handleStop);
    };
  }, [username]);

  // Voice-Channel-Mitgliedschaft steuert Mic
  useEffect(() => {
    const handleVoiceUpdate = (voiceChannels) => {
      let foundChannel = null;

      Object.entries(voiceChannels).forEach(([ch, users]) => {
        if (users.some((u) => u.username === username)) {
          foundChannel = ch;
        }
      });

      // User ist in einem Voice-Channel
      if (foundChannel && !inCallRef.current) {
        startCall().then((stream) => {
          streamRef.current = stream;
          inCallRef.current = true;
          setInCall(true);
          setMuted(false);
          setVoiceChannel(foundChannel);
        });
      }

      // User ist in keinem Voice-Channel mehr
      if (!foundChannel && inCallRef.current) {
        stopCall();
        if (streamRef.current) {
          streamRef.current.getTracks().forEach((t) => t.stop());
          streamRef.current = null;
        }
        inCallRef.current = false;
        setInCall(false);
        setMuted(false);
        setSpeaking(false);
        setVoiceChannel(null);
      }
    };

    socket.on("voiceChannelUpdate", handleVoiceUpdate);
    return () => socket.off("voiceChannelUpdate", handleVoiceUpdate);
  }, [username]);

  const toggleMute = () => {
    if (!streamRef.current) return;
    const tracks = streamRef.current.getAudioTracks();
    if (!tracks.length) return;

    const enabled = tracks[0].enabled;
    tracks.forEach((t) => (t.enabled = !enabled));
    setMuted(enabled);
  };

  // Manuelles Trennen (X) ‚Üí Voice-Channel verlassen
  const manualDisconnect = () => {
    const ch = localStorage.getItem("voiceChannel");
    if (ch) {
      socket.emit("leaveVoiceChannel", { channel: ch });
      localStorage.removeItem("voiceChannel");
    }
    // Rest erledigt der voiceChannelUpdate-Listener
  };

  return (
    <div
      style={{
        height: "90px",
        background: "#232428",
        display: "flex",
        alignItems: "center",
        padding: "10px",
        color: "white"
      }}
    >
      <div
        className={speaking ? "speaking-ring" : ""}
        style={{
          width: 40,
          height: 40,
          borderRadius: "50%",
          overflow: "hidden",
          marginRight: 10
        }}
      >
        <img src="/avatar.png" className="avatar" alt="" />
      </div>

      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: "bold" }}>{username}</div>
        <div style={{ fontSize: 12, color: "#b9bbbe" }}>
          {!voiceChannel
            ? "Nicht verbunden"
            : muted
            ? `Stummgeschaltet in ${voiceChannel}`
            : speaking
            ? `Spricht in ${voiceChannel}...`
            : `Verbunden in ${voiceChannel}`}
        </div>
      </div>

      {inCall && (
        <>
          <button onClick={toggleMute} style={btnStyle}>
            {muted ? "üîá" : "üé§"}
          </button>
          <button onClick={manualDisconnect} style={btnStyle}>
            ‚ùå
          </button>
        </>
      )}
    </div>
  );
}

const btnStyle = {
  background: "transparent",
  border: "none",
  color: "white",
  fontSize: 20,
  cursor: "pointer",
  marginLeft: 8
};
