import React, { useState, useEffect, useRef } from "react";
import { socket } from "../socket";
import {
  uploadAvatar,
  updateAvatarOnServer,
  getAvatarFromServer
} from "../avatar";

export default function VoicePanel() {
  const [inCall, setInCall] = useState(false);
  const [muted, setMuted] = useState(false);
  const [speaking, setSpeaking] = useState(false);
  const [voiceChannel, setVoiceChannel] = useState(null);
  const [users, setUsers] = useState({});

  const streamRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const rafRef = useRef(null);
  const lastSpeakingRef = useRef(false);

  const username = localStorage.getItem("username");

  // Avatare empfangen
  useEffect(() => {
    const handler = (allUsers) => setUsers(allUsers);
    socket.on("avatarUpdate", handler);
    return () => socket.off("avatarUpdate", handler);
  }, []);

  const avatar = getAvatarFromServer(users, username);

  // Voice-Channel-Updates
  useEffect(() => {
    const handler = (voiceChannels) => {
      let found = null;

      Object.entries(voiceChannels).forEach(([ch, list]) => {
        if (list.some((u) => u.username === username)) {
          found = ch;
        }
      });

      if (found && !inCall) startCall(found);
      if (!found && inCall) stopCall();
    };

    socket.on("voiceChannelUpdate", handler);
    return () => socket.off("voiceChannelUpdate", handler);
  }, [inCall, username]);

  // Call starten
  const startCall = async (channel) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;
      setInCall(true);
      setMuted(false);
      setVoiceChannel(channel);
      setupAnalyser(stream, channel);
    } catch (err) {
      console.error("Mic error:", err);
    }
  };

  // Call stoppen
  const stopCall = () => {
    if (rafRef.current) cancelAnimationFrame(rafRef.current);
    if (audioContextRef.current) audioContextRef.current.close();

    if (streamRef.current) {
      streamRef.current.getTracks().forEach((t) => t.stop());
      streamRef.current = null;
    }

    setInCall(false);
    setMuted(false);
    setSpeaking(false);
    setVoiceChannel(null);
    lastSpeakingRef.current = false;
  };

  // Audio-Analyser
  const setupAnalyser = (stream, channel) => {
    const audioContext = new AudioContext();
    const source = audioContext.createMediaStreamSource(stream);
    const analyser = audioContext.createAnalyser();
    analyser.fftSize = 512;

    const data = new Uint8Array(analyser.frequencyBinCount);

    source.connect(analyser);

    audioContextRef.current = audioContext;
    analyserRef.current = analyser;

    const loop = () => {
      analyser.getByteFrequencyData(data);
      const avg = data.reduce((a, b) => a + b, 0) / data.length;

      const isSpeaking = avg > 20 && !muted && inCall;

      if (isSpeaking !== lastSpeakingRef.current) {
        lastSpeakingRef.current = isSpeaking;
        setSpeaking(isSpeaking);

        socket.emit(isSpeaking ? "speakingStart" : "speakingStop", {
          channel,
          user: username
        });
      }

      rafRef.current = requestAnimationFrame(loop);
    };

    loop();
  };

  const toggleMute = () => {
    if (!streamRef.current) return;
    const track = streamRef.current.getAudioTracks()[0];
    track.enabled = !track.enabled;
    setMuted(!track.enabled);
  };

  const disconnect = () => {
    if (!voiceChannel) return;
    socket.emit("leaveVoiceChannel", { channel: voiceChannel });
  };

  const handleAvatarRightClick = (e) => {
    e.preventDefault();
    uploadAvatar((base64) => {
      updateAvatarOnServer(username, {
        avatarType: "image",
        avatarColor: avatar.avatarColor,
        avatarLetter: avatar.avatarLetter,
        avatarImage: base64
      });
    });
  };

  return (
    <div
      style={{
        height: "90px",
        background: "#232428",
        display: "flex",
        alignItems: "center",
        padding: "10px",
        color: "white"
      }}
    >
      <div
        onContextMenu={handleAvatarRightClick}
        className={speaking ? "speaking-ring" : ""}
        style={{
          width: 40,
          height: 40,
          borderRadius: "50%",
          overflow: "hidden",
          marginRight: 10,
          background:
            avatar.avatarType === "default" ? avatar.avatarColor : "transparent",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontWeight: "bold",
          fontSize: 20,
          color: "white",
          cursor: "pointer"
        }}
      >
        {avatar.avatarType === "image" ? (
          <img src={avatar.avatarImage} className="avatar" alt="" />
        ) : (
          avatar.avatarLetter
        )}
      </div>

      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: "bold" }}>{username}</div>
        <div style={{ fontSize: 12, color: "#b9bbbe" }}>
          {!voiceChannel
            ? "Nicht verbunden"
            : speaking
            ? `Spricht in ${voiceChannel}...`
            : muted
            ? `Stummgeschaltet in ${voiceChannel}`
            : `Verbunden in ${voiceChannel}`}
        </div>
      </div>

      {inCall && (
        <>
          <button
            onClick={toggleMute}
            style={{
              background: "transparent",
              border: "none",
              color: "white",
              fontSize: 20,
              cursor: "pointer",
              marginLeft: 8
            }}
          >
            {muted ? "üîá" : "üé§"}
          </button>

          <button
            onClick={disconnect}
            style={{
              background: "transparent",
              border: "none",
              color: "white",
              fontSize: 20,
              cursor: "pointer",
              marginLeft: 8
            }}
          >
            ‚ùå
          </button>
        </>
      )}
    </div>
  );
}
