import React, { useEffect } from "react";
import { registerUser } from "./socket";   // socket wird nicht mehr direkt importiert
import "./App.css";

function App() {
  useEffect(() => {
    let username = localStorage.getItem("username");

    // Falls kein Username existiert → nachfragen
    if (!username || username.trim() === "") {
      username = prompt("Bitte gib deinen Namen ein:");

      if (!username || username.trim() === "") {
        username = "User" + Math.floor(Math.random() * 9999);
      }

      localStorage.setItem("username", username);
    }

    // Jetzt korrekt registrieren
    registerUser();
  }, []);

  return (
    <div className="App">
      {/* Dein komplettes Layout */}
    </div>
  );
}

export default App;
