import React, { useState, useEffect } from "react";
import { socket } from "../socket";
import { startCall } from "../webrtc";

export default function ChannelList({ currentChannel, onSelect }) {
  const [textChannels, setTextChannels] = useState(["admin", "general", "gaming", "music"]);
  const [voiceChannelKeys, setVoiceChannelKeys] = useState(["admin", "general", "gaming", "music"]);
  const [activeVoice, setActiveVoice] = useState(null);

  useEffect(() => {
    const handleUpdate = ({ textChannels, voiceChannels }) => {
      setTextChannels(textChannels);
      setVoiceChannelKeys(Object.keys(voiceChannels));
    };

    socket.on("channelListUpdate", handleUpdate);
    return () => socket.off("channelListUpdate", handleUpdate);
  }, []);

  const joinVoice = async (name) => {
    const channel = name.toLowerCase();

    if (activeVoice !== channel) {
      if (activeVoice) {
        socket.emit("leaveVoiceChannel", { channel: activeVoice });
      }

      socket.emit("joinVoiceChannel", {
        channel,
        username: localStorage.getItem("username")
      });

      setActiveVoice(channel);
      localStorage.setItem("voiceChannel", channel);

      // WebRTC automatisch starten
      await startCall(() => {});
    }
  };

  return (
    <div style={{ padding: 10, color: "white" }}>
      <div style={{ marginBottom: 10, opacity: 0.6 }}>TEXT CHANNELS</div>

      {textChannels.map((ch) => (
        <div
          key={ch}
          onClick={() => onSelect(ch)}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            background: currentChannel === ch ? "#3a3c41" : "transparent",
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          # {ch}
        </div>
      ))}

      <div style={{ marginTop: 20, marginBottom: 10, opacity: 0.6 }}>VOICE CHANNELS</div>

      {voiceChannelKeys.map((vc) => (
        <div
          key={vc}
          onClick={() => joinVoice(vc)}
          style={{
            padding: "6px 10px",
            borderRadius: 4,
            background: activeVoice === vc.toLowerCase() ? "#3a3c41" : "transparent",
            cursor: "pointer",
            marginBottom: 4
          }}
        >
          ðŸ”Š {vc}
        </div>
      ))}
    </div>
  );
}
