import React, { useEffect, useState } from "react";
import { socket } from "../socket";

export default function UserList() {
  const username = localStorage.getItem("username");

  const [voiceChannels, setVoiceChannels] = useState({});
  const [users, setUsers] = useState({});
  const [speakingUsers, setSpeakingUsers] = useState({});
  const [myVoiceChannel, setMyVoiceChannel] = useState(null);

  useEffect(() => {
    const handleVoiceUpdate = (channels) => {
      setVoiceChannels(channels);

      // herausfinden, in welchem Channel ich bin
      let found = null;
      Object.entries(channels).forEach(([ch, list]) => {
        if (list.some((u) => u.username === username)) {
          found = ch;
        }
      });
      setMyVoiceChannel(found);
    };

    socket.on("voiceChannelUpdate", handleVoiceUpdate);
    socket.on("avatarUpdate", setUsers);

    socket.on("speakingStart", ({ user }) =>
      setSpeakingUsers((p) => ({ ...p, [user]: true }))
    );

    socket.on("speakingStop", ({ user }) =>
      setSpeakingUsers((p) => ({ ...p, [user]: false }))
    );

    return () => {
      socket.off("voiceChannelUpdate", handleVoiceUpdate);
      socket.off("avatarUpdate", setUsers);
      socket.off("speakingStart");
      socket.off("speakingStop");
    };
  }, [username]);

  return (
    <div>
      <div style={{ opacity: 0.6, marginBottom: 10 }}>VOICE USERS</div>

      {Object.keys(voiceChannels).map((channel) => {
        const isMyChannel = channel === myVoiceChannel;
        const av = users[username] || {
          avatarType: "default",
          avatarColor: "#5865F2",
          avatarLetter: username?.[0]?.toUpperCase() || "?",
          avatarImage: null
        };
        const isSpeaking = speakingUsers[username];

        return (
          <div key={channel} style={{ marginBottom: 15 }}>
            <div style={{ fontSize: 12, opacity: 0.7, marginBottom: 4 }}>
              {channel}
            </div>

            {isMyChannel && (
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  marginBottom: 4
                }}
              >
                <div
                  className={isSpeaking ? "speaking-ring" : undefined}
                  style={{
                    width: 28,
                    height: 28,
                    borderRadius: "50%",
                    overflow: "hidden",
                    marginRight: 8,
                    background:
                      av.avatarType === "default"
                        ? av.avatarColor
                        : "transparent",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontWeight: "bold",
                    fontSize: 14
                  }}
                >
                  {av.avatarType === "image" ? (
                    <img src={av.avatarImage} className="avatar" alt="" />
                  ) : (
                    av.avatarLetter
                  )}
                </div>

                <div>{username}</div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}
