// src/components/ChannelList.jsx
import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3000";

function ChannelList({ currentChannel, onSelectChannel }) {
  const [channels, setChannels] = useState([]);
  const [showCreate, setShowCreate] = useState(false);
  const [newChannelName, setNewChannelName] = useState("");

  async function loadChannels() {
    try {
      const res = await fetch(`${API_BASE}/api/channels/list`);
      const data = await res.json();
      if (data.ok) {
        setChannels(data.channels);
      }
    } catch (err) {
      console.error("Fehler beim Laden der Channels:", err);
    }
  }

  async function handleCreateChannel(e) {
    e.preventDefault();
    if (!newChannelName.trim()) return;

    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Keine Session – bitte neu einloggen.");
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/channels/create`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-session-id": sessionId
        },
        body: JSON.stringify({ name: newChannelName.trim() })
      });

      const data = await res.json();
      if (!data.ok) {
        alert(data.error || "Channel konnte nicht erstellt werden.");
        return;
      }

      setNewChannelName("");
      setShowCreate(false);
      await loadChannels();
    } catch (err) {
      console.error("Fehler beim Erstellen des Channels:", err);
    }
  }

  useEffect(() => {
    loadChannels();
  }, []);

  return (
    <div className="channel-list">
      <div className="channel-list-header">
        <span>Text-Channels</span>
        <button onClick={() => setShowCreate(true)}>+</button>
      </div>

      <div className="channel-list-items">
        {channels.map((ch) => (
          <div
            key={ch}
            className={
              "channel-item" + (currentChannel === ch ? " channel-item-active" : "")
            }
            onClick={() => onSelectChannel(ch)}
          >
            #{ch}
          </div>
        ))}
      </div>

      {showCreate && (
        <div className="channel-create-modal">
          <div className="channel-create-content">
            <h4>Neuen Channel erstellen</h4>
            <form onSubmit={handleCreateChannel}>
              <input
                type="text"
                value={newChannelName}
                onChange={(e) => setNewChannelName(e.target.value)}
                placeholder="channel-name"
              />
              <div className="channel-create-actions">
                <button type="button" onClick={() => setShowCreate(false)}>
                  Abbrechen
                </button>
                <button type="submit">Erstellen</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ChannelList;
